<?phprequire('phpmailer/class.phpmailer.php');require('phpmailer/class.pop3.php');require('phpmailer/class.smtp.php');require('phpmailer/PHPMailerAutoload.php');//$subject="WB test";//$message="sdf dfg dfg";//$email="dilanka.test@gmail.com,dilanka.champ@gmail.com";//$emails=array("dilanka.test@gmail.com","dilanka.champ@gmail.com");//sendEmail($subject, $message, $emails);function sendEmail($subject, $message, $emails, $file_path = null, $file_name = null) {    //Create a new PHPMailer instance    $mail = new PHPMailer();    //Tell PHPMailer to use SMTP    $mail->isSMTP();    //Enable SMTP debugging    // 0 = off (for production use)    // 1 = client messages    // 2 = client and server messages    $mail->SMTPDebug = 0;    //Ask for HTML-friendly debug output    $mail->Encoding = '7bit';    $mail->ContentType = 'text/html';    $mail->Debugoutput = 'html';    $mail->CharSet = 'UTF-8';    //Set the hostname of the mail server    $mail->Host = "smtpout.secureserver.net";    //Set the SMTP port number - likely to be 25, 465 or 587    $mail->Port = 80;    //Whether to use SMTP authentication    $mail->SMTPAuth = true;    //Username to use for SMTP authentication    $mail->Username = "stars@kenolee.com";    //Password to use for SMTP authentication    $mail->Password = "Password#01";    //Set who the message is to be sent from    $mail->setFrom('info@nanaska.com', 'nanaska');    //Set who the message is to be sent to				    //$mail->addAddress($email);    //Set the subject line    $mail->Subject = ($subject);    //Read an HTML message body from an external file, convert referenced images to embedded,    //convert HTML into a basic plain-text alternative body    $mail->msgHTML($message);    if ($file_path != "" && $file_name != "") {        $mail->AddAttachment($file_path, $file_name);    }    if (is_array($emails)) {        //for multiple emails        foreach ($emails as $email) {            $mail->addAddress($email);        }    } else {        //for single email        $email = $emails;        $mail->addAddress($email);    }    $mail->send();}